// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 < 0.9.0;

contract PizzaToken {
    string public name = "PizzaToken";
    string public symbol = "PIZ";

    uint256 public totalSupply;

    // Register an event
    event Transfer (
        address indexed _from,
        address indexed _to,
        uint256 _value
    );

    // Approval event
    event Approval (
        address indexed _owner,
        address indexed _spender,
        uint256 _value
    );

    mapping(address => uint256) public balance;
    // Allowance
    mapping(address => mapping(address => uint256)) public allowance ;

    constructor(uint256 _initialSupply) public {
        balance[msg.sender] = _initialSupply ;
        totalSupply = _initialSupply;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balance[msg.sender] >= _value);

        // deducts from the sender's account
        balance[msg.sender] -= _value;

        // adds to the receiver's account
        balance[_to] += _value;

        // Fire the event
        emit Transfer(msg.sender, _to, _value);

        return true ;
    }

    // Delegate transfer function

    // Approve
    function approve(address _spender, uint256 _value) public returns (bool success) {
        
        // Handle Allowance
        allowance[msg.sender][_spender] = _value;

        // Trigger the approval event
        emit Approval(msg.sender, _spender, _value) ;

        return true ;
    }

    // Transfer from function
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        // Make the original owner has more than what you are trying to transfer
        require(balance[_from] >= _value);

        // Make sure the spender does not spend more than approved allowance
        // msg.value is the third party here
        require(allowance[_from][msg.sender] >= _value) ; 

        // deducts from the senders account
        balance[_from] -= _value;

        // Adds to the receivers account
        balance[_to] += _value;

        // Emit transfer event
        emit Transfer(_from, _to, _value) ;

        return true;
    }
}
